import groovy.lang.GroovyObjectimport java.lang.Objectimport java.lang.Exceptionimport java.lang.Class import org.marker.ext.ModuleActivator;import org.marker.ext.module.ModuleContext;import org.marker.ext.ModuleUtilsimport org.marker.mushroom.ext.model.ContentModel;import org.marker.mushroom.ext.model.ContentModelContext;/** * 帮助模块 *  * @author marker *  */public class ModuleActivatorImpl extends ModuleUtils implements ModuleActivator {		/**	 * 启用 	 */	public void start(ModuleContext context) throws Exception { 		GroovyObject cm = util.loadModelObject("model");		ContentModelContext.getInstance().put(cm);	} 			/**	 * 停止	 */	public void stop(ModuleContext context) throws Exception { 		ContentModelContext.getInstance().remove("help");	}	}